DROP TABLE IF EXISTS Bible;
CREATE TABLE Bible (
  bibleId TEXT NOT NULL PRIMARY KEY,
  abbr TEXT NOT NULL,
  iso3 TEXT NOT NULL REFERENCES Language(iso3),
  name TEXT NOT NULL,
  englishName TEXT NULL,
  direction TEXT CHECK (direction IN('ltr','rtl')) default('ltr'),
  fontClass TEXT NULL,
  script TEXT NULL,
  country TEXT NULL REFERENCES Country(code),
  stylesheet TEXT NULL,
  redistribute TEXT CHECK (redistribute IN('T', 'F')) default('F'),
  objectKey TEXT NOT NULL,
  organizationId TEXT NULL REFERENCES Owner(ownerCode),
  ssFilename TEXT NULL,
  hasHistory TEXT CHECK (hasHistory IN('T','F')) default('F'),
  copyright TEXT NULL,
  introduction TEXT NULL);
